patch:
diff --git a/src/H5Shyper.c b/src/H5Shyper.c
index 7d6739a7fb..e28a9a8fb3 100644
--- a/src/H5Shyper.c
+++ b/src/H5Shyper.c
@@ -4677,6 +4677,12 @@ H5S__get_select_hyper_blocklist(H5S_t *space, hsize_t startblock, hsize_t numblo

         /* Set some convenience values */
         ndims    = space->extent.rank;
+        
+        /* Check for valid rank to prevent underflow */
+        if (ndims == 0) {
+            ret_value = FAIL;
+            goto done;
+        }
         fast_dim = ndims - 1;

         /* Check which set of dimension information to use */
@@ -4694,6 +4700,12 @@ H5S__get_select_hyper_blocklist(H5S_t *space, hsize_t startblock, hsize_t numblo
              */
             diminfo = space->select.sel_info.hslab->diminfo.app;

+        /* Check for valid diminfo to prevent null pointer dereference */
+        if (diminfo == NULL) {
+            ret_value = FAIL;
+            goto done;
+        }
+
         /* Build the tables of count sizes as well as the initial offset */
         for (u = 0; u < ndims; u++) {
             tmp_count[u] = diminfo[u].count;
@@ -4795,6 +4807,7 @@ H5S__get_select_hyper_blocklist(H5S_t *space, hsize_t startblock, hsize_t numblo
                                               &startblock, &numblocks, &buf);
     } /* end else */

+done:
     FUNC_LEAVE_NOAPI(ret_value)
 } /* end H5S__get_select_hyper_blocklist() */
