+++ b/src/H5Shyper.c
@@ -4677,6 +4677,10 @@ H5S__get_select_hyper_blocklist(H5S_t *space, hsize_t startblock, hsize_t numblo

         /* Set some convenience values */
         ndims    = space->extent.rank;
