patch:
diff --git a/icu4c/source/i18n/persncal.cpp b/icu4c/source/i18n/persncal.cpp
index f66ac676a4a..da805ed37a6 100644
--- a/icu4c/source/i18n/persncal.cpp
+++ b/icu4c/source/i18n/persncal.cpp
@@ -213,7 +213,21 @@ void PersianCalendar::handleComputeFields(int32_t julianDay, UErrorCode &/*statu
     int32_t year, month, dayOfMonth, dayOfYear;

     int32_t daysSinceEpoch = julianDay - PERSIAN_EPOCH;
-    year = 1 + ClockMath::floorDivide(33 * daysSinceEpoch + 3, 12053);
+    // Prevent signed integer overflow by using int64_t for the calculation
+    auto floorDivide64 = [](int64_t num, int64_t denom) -> int32_t {
+        if (denom == 0) return 0; // avoid division by zero, should not happen
+        int64_t q;
+        if (num >= 0) {
+            q = num / denom;
+        } else {
+            q = ((num + 1) / denom) - 1;
+        }
+        // Clamp to int32_t range
+        if (q < INT32_MIN) q = INT32_MIN;
+        if (q > INT32_MAX) q = INT32_MAX;
+        return static_cast<int32_t>(q);
+    };
+    year = 1 + floorDivide64(33LL * static_cast<int64_t>(daysSinceEpoch) + 3LL, 12053LL);

     int32_t farvardin1 = 365 * (year - 1) + ClockMath::floorDivide(8 * year + 21, 33);
     dayOfYear = (daysSinceEpoch - farvardin1); // 0-based
