patch:
diff --git a/icu4c/source/i18n/islamcal.cpp b/icu4c/source/i18n/islamcal.cpp
index 4fd0e07d920..00054efd626 100644
--- a/icu4c/source/i18n/islamcal.cpp
+++ b/icu4c/source/i18n/islamcal.cpp
@@ -609,11 +609,17 @@ void IslamicCalendar::handleComputeFields(int32_t julianDay, UErrorCode &status)
     int32_t startDate;
     int32_t days = julianDay - CIVIL_EPOC;

-    if (cType == CIVIL || cType == TBLA) {
+        if (cType == CIVIL || cType == TBLA) {
         if(cType == TBLA) {
             days = julianDay - ASTRONOMICAL_EPOC;
-        }
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+         }
         // Use the civil calendar approximation, which is just arithmetic
+        // Check for overflow before performing 30 * days + 10646
+        if (days > (2147483647 - 10646) / 30) {
+            status = U_ILLEGAL_ARGUMENT_ERROR;
+            return;
+        }
         year  = (int)ClockMath::floorDivide( (double)(30 * days + 10646) , 10631.0 );
         month = (int32_t)uprv_ceil((days - 29 - yearStart(year)) / 29.5 );
         month = month<11?month:11;
@@ -647,6 +653,11 @@ void IslamicCalendar::handleComputeFields(int32_t julianDay, UErrorCode &status)
         int32_t umalquraStartdays = yearStart(UMALQURA_YEAR_START) ;
         if( days < umalquraStartdays){
                 //Use Civil calculation
+                // Check for overflow before performing 30 * days + 10646
+                if (days > (2147483647 - 10646) / 30) {
+                    status = U_ILLEGAL_ARGUMENT_ERROR;
+                    return;
+                }
                 year  = (int)ClockMath::floorDivide( (double)(30 * days + 10646) , 10631.0 );
                 month = (int32_t)uprv_ceil((days - 29 - yearStart(year)) / 29.5 );
                 month = month<11?month:11;
