patch:
diff --git a/icu4c/source/i18n/regexcmp.cpp b/icu4c/source/i18n/regexcmp.cpp
index a4c12804237..a19f0bba04d 100644
--- a/icu4c/source/i18n/regexcmp.cpp
+++ b/icu4c/source/i18n/regexcmp.cpp
@@ -4019,9 +4019,18 @@ void RegexCompile::nextChar(RegexPatternChar &c) {
         c.fQuoted = TRUE;
         if ((c.fChar==chBackSlash && peekCharLL()==chE && ((fModeFlags & UREGEX_LITERAL) == 0)) ||
             c.fChar == (UChar32)-1) {
-            fQuoteMode = FALSE;  //  Exit quote mode,
-            nextCharLL();        // discard the E
-            nextChar(c);         // recurse to get the real next char
+            // Exit quote mode, discard the E, and iteratively get the next char
+            do {
+                fQuoteMode = FALSE;
+                nextCharLL(); // discard the E
+                fScanIndex = UTEXT_GETNATIVEINDEX(fRXPat->fPattern);
+                c.fChar    = nextCharLL();
+                c.fQuoted  = FALSE;
+                if (fQuoteMode) {
+                    c.fQuoted = TRUE;
+                }
+            } while (fQuoteMode &&
+                     (c.fChar==chBackSlash && peekCharLL()==chE && ((fModeFlags & UREGEX_LITERAL) == 0)));
         }
     }
     else if (fInBackslashQuote) {
@@ -4140,7 +4149,22 @@ void RegexCompile::nextChar(RegexPatternChar &c) {
                 //  "\Q"  enter quote mode, which will continue until "\E"
                 fQuoteMode = TRUE;
                 nextCharLL();       // discard the 'Q'.
-                nextChar(c);        // recurse to get the real next char.
+                // Iteratively get the next char, handling immediate quote mode exit
+                do {
+                    fScanIndex = UTEXT_GETNATIVEINDEX(fRXPat->fPattern);
+                    c.fChar    = nextCharLL();
+                    c.fQuoted  = FALSE;
+                    if (fQuoteMode) {
+                        c.fQuoted = TRUE;
+                    }
+                    if (fQuoteMode &&
+                        (c.fChar==chBackSlash && peekCharLL()==chE && ((fModeFlags & UREGEX_LITERAL) == 0))) {
+                        fQuoteMode = FALSE;
+                        nextCharLL(); // discard the E
+                    } else {
+                        break;
+                    }
+                } while (true);
             }
             else
             {
