patch:
diff --git a/icu4c/source/i18n/regexcmp.cpp b/icu4c/source/i18n/regexcmp.cpp
index a4c12804237..cf3680f4d25 100644
--- a/icu4c/source/i18n/regexcmp.cpp
+++ b/icu4c/source/i18n/regexcmp.cpp
@@ -4010,29 +4010,29 @@ UChar32  RegexCompile::peekCharLL() {
 //
 //------------------------------------------------------------------------------
 void RegexCompile::nextChar(RegexPatternChar &c) {
-
-    fScanIndex = UTEXT_GETNATIVEINDEX(fRXPat->fPattern);
-    c.fChar    = nextCharLL();
-    c.fQuoted  = FALSE;
-
-    if (fQuoteMode) {
-        c.fQuoted = TRUE;
-        if ((c.fChar==chBackSlash && peekCharLL()==chE && ((fModeFlags & UREGEX_LITERAL) == 0)) ||
-            c.fChar == (UChar32)-1) {
-            fQuoteMode = FALSE;  //  Exit quote mode,
-            nextCharLL();        // discard the E
-            nextChar(c);         // recurse to get the real next char
+    for (;;) {
+        fScanIndex = UTEXT_GETNATIVEINDEX(fRXPat->fPattern);
+        c.fChar    = nextCharLL();
+        c.fQuoted  = FALSE;
+
+        if (fQuoteMode) {
+            c.fQuoted = TRUE;
+            if ((c.fChar==chBackSlash && peekCharLL()==chE && ((fModeFlags & UREGEX_LITERAL) == 0)) ||
+                c.fChar == (UChar32)-1) {
+                fQuoteMode = FALSE;  //  Exit quote mode,
+                nextCharLL();        // discard the E
+                continue;            // loop to get the real next char
+            }
         }
-    }
-    else if (fInBackslashQuote) {
-        // The current character immediately follows a '\'
-        // Don't check for any further escapes, just return it as-is.
-        // Don't set c.fQuoted, because that would prevent the state machine from
-        //    dispatching on the character.
-        fInBackslashQuote = FALSE;
-    }
-    else
-    {
+        else if (fInBackslashQuote) {
+            // The current character immediately follows a '\'
+            // Don't check for any further escapes, just return it as-is.
+            // Don't set c.fQuoted, because that would prevent the state machine from
+            //    dispatching on the character.
+            fInBackslashQuote = FALSE;
+        }
+        else
+        {
         // We are not in a \Q quoted region \E of the source.
         //
         if (fModeFlags & UREGEX_COMMENTS) {
@@ -4140,7 +4140,7 @@ void RegexCompile::nextChar(RegexPatternChar &c) {
                 //  "\Q"  enter quote mode, which will continue until "\E"
                 fQuoteMode = TRUE;
                 nextCharLL();       // discard the 'Q'.
-                nextChar(c);        // recurse to get the real next char.
+                continue;           // loop to get the real next char
             }
             else
             {
@@ -4150,6 +4150,8 @@ void RegexCompile::nextChar(RegexPatternChar &c) {
                 fInBackslashQuote = TRUE;
             }
         }
+        }
+        break;
     }

     // re-enable # to end-of-line comments, in case they were disabled.
