patch:
diff --git a/icu4c/source/common/ustring.cpp b/icu4c/source/common/ustring.cpp
index 7ab2e1bf891..61fef1cc4e8 100644
--- a/icu4c/source/common/ustring.cpp
+++ b/icu4c/source/common/ustring.cpp
@@ -1206,6 +1206,15 @@ static int8_t _digit16(UChar c) {
     return -1;
 }

+/* Maximum recursion depth for escape sequence parsing to prevent stack overflow */
+#define MAX_UNESCAPE_RECURSION_DEPTH 100
+
+/* Internal function with recursion depth tracking */
+static UChar32 u_unescapeAt_internal(UNESCAPE_CHAR_AT charAt,
+                                     int32_t *offset,
+                                     int32_t length,
+                                     void *context,
+                                     int32_t depth);
 /* Parse a single escape sequence.  Although this method deals in
  * UChars, it does not use C++ or UnicodeString.  This allows it to
  * be used from C contexts. */
@@ -1214,6 +1223,14 @@ u_unescapeAt(UNESCAPE_CHAR_AT charAt,
              int32_t *offset,
              int32_t length,
              void *context) {
+    return u_unescapeAt_internal(charAt, offset, length, context, 0);
+}
+
+static UChar32 u_unescapeAt_internal(UNESCAPE_CHAR_AT charAt,
+                                     int32_t *offset,
+                                     int32_t length,
+                                     void *context,
+                                     int32_t depth) {

     int32_t start = *offset;
     UChar c;
@@ -1226,6 +1243,11 @@ u_unescapeAt(UNESCAPE_CHAR_AT charAt,
     int32_t i;
     UBool braces = FALSE;

+    /* Check recursion depth to prevent stack overflow */
+    if (depth >= MAX_UNESCAPE_RECURSION_DEPTH) {
+        goto err;
+    }
+
     /* Check that offset is in range */
     if (*offset < 0 || *offset >= length) {
         goto err;
@@ -1294,7 +1316,7 @@ u_unescapeAt(UNESCAPE_CHAR_AT charAt,
             int32_t ahead = *offset + 1;
             c = charAt(*offset, context);
             if (c == 0x5C /*'\\'*/ && ahead < length) {
-                c = (UChar) u_unescapeAt(charAt, &ahead, length, context);
+                c = (UChar) u_unescapeAt_internal(charAt, &ahead, length, context, depth + 1);
             }
             if (U16_IS_TRAIL(c)) {
                 *offset = ahead;
