patch:
diff --git a/icu4c/source/i18n/chnsecal.cpp b/icu4c/source/i18n/chnsecal.cpp
index 17d92ed1a3d..7c777288906 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -233,7 +233,13 @@ int32_t ChineseCalendar::handleGetExtendedYear(UErrorCode& status) {
     } else {
         int32_t cycle = internalGet(UCAL_ERA, 1) - 1; // 0-based cycle
         // adjust to the instance specific epoch
-        year = cycle * 60 + internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        int64_t yearLong = static_cast<int64_t>(cycle) * 60 + internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        // Check for overflow
+        if (yearLong > INT32_MAX || yearLong < INT32_MIN) {
+            status = U_ILLEGAL_ARGUMENT_ERROR;
+            return 0;
+        }
+        year = static_cast<int32_t>(yearLong);
     }
     return year;
 }
