patch:
diff --git a/icu4c/source/i18n/chnsecal.cpp b/icu4c/source/i18n/chnsecal.cpp
index 17d92ed1a3d..feb3e25b419 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -232,8 +232,16 @@ int32_t ChineseCalendar::handleGetExtendedYear(UErrorCode& status) {
         year = internalGet(UCAL_EXTENDED_YEAR, 1); // Default to year 1
     } else {
         int32_t cycle = internalGet(UCAL_ERA, 1) - 1; // 0-based cycle
+        // Check for overflow in cycle * 60
+        int32_t cycleYears;
+        if (uprv_mul32_overflow(cycle, 60, &cycleYears)) {
+            status = U_ILLEGAL_ARGUMENT_ERROR;
+            return 0;
+        }
         // adjust to the instance specific epoch
-        year = cycle * 60 + internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        int32_t yearInCycle = internalGet(UCAL_YEAR, 1);
+        int32_t epochAdjustment = fEpochYear - CHINESE_EPOCH_YEAR;
+        year = cycleYears + yearInCycle - epochAdjustment;
     }
     return year;
 }
