patch:
diff --git a/icu4c/source/i18n/chnsecal.cpp b/icu4c/source/i18n/chnsecal.cpp
index 17d92ed1a3d..b3d1c6ee536 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -233,7 +233,24 @@ int32_t ChineseCalendar::handleGetExtendedYear(UErrorCode& status) {
     } else {
         int32_t cycle = internalGet(UCAL_ERA, 1) - 1; // 0-based cycle
         // adjust to the instance specific epoch
-        year = cycle * 60 + internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        // Check for overflow before multiplication
+        if (cycle > 0 && cycle > (INT32_MAX - 59) / 60) {
+            status = U_ILLEGAL_ARGUMENT_ERROR;
+            return 0;
+        }
+        if (cycle < 0 && cycle < (INT32_MIN + 59) / 60) {
+            status = U_ILLEGAL_ARGUMENT_ERROR;
+            return 0;
+        }
+        int32_t cycle60 = cycle * 60;
+        int32_t yearOfCycle = internalGet(UCAL_YEAR, 1);
+        // Check for overflow in addition
+        if ((cycle60 > 0 && yearOfCycle > INT32_MAX - cycle60) ||
+            (cycle60 < 0 && yearOfCycle < INT32_MIN - cycle60)) {
+            status = U_ILLEGAL_ARGUMENT_ERROR;
+            return 0;
+        }
+        year = cycle60 + yearOfCycle - (fEpochYear - CHINESE_EPOCH_YEAR);
     }
     return year;
 }
