patch:
diff --git a/icu4c/source/i18n/chnsecal.cpp b/icu4c/source/i18n/chnsecal.cpp
index 17d92ed1a3d..80360224ab8 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -233,7 +233,15 @@ int32_t ChineseCalendar::handleGetExtendedYear(UErrorCode& status) {
     } else {
         int32_t cycle = internalGet(UCAL_ERA, 1) - 1; // 0-based cycle
         // adjust to the instance specific epoch
-        year = cycle * 60 + internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        int64_t year64 = static_cast<int64_t>(cycle) * 60 + internalGet(UCAL_YEAR, 1) - (static_cast<int64_t>(fEpochYear) - static_cast<int64_t>(CHINESE_EPOCH_YEAR));
+        // Clamp to int32_t range
+        if (year64 > INT32_MAX) {
+            year = INT32_MAX;
+        } else if (year64 < INT32_MIN) {
+            year = INT32_MIN;
+        } else {
+            year = static_cast<int32_t>(year64);
+        }
     }
     return year;
 }
