patch:
diff --git a/icu4c/source/i18n/chnsecal.cpp b/icu4c/source/i18n/chnsecal.cpp
index 17d92ed1a3d..8d429b92aaa 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -233,9 +233,16 @@ int32_t ChineseCalendar::handleGetExtendedYear(UErrorCode& status) {
     } else {
         int32_t cycle = internalGet(UCAL_ERA, 1) - 1; // 0-based cycle
         // adjust to the instance specific epoch
-        year = cycle * 60 + internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        int32_t year_offset = internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        if (cycle > INT32_MAX / 60) {
+            // Overflow would occur in multiplication
+            year = INT32_MAX;
+        } else {
+            year = cycle * 60 + year_offset;
+        }
     }
-    return year;
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+     return year;
 }

 /**
@@ -247,8 +254,7 @@ int32_t ChineseCalendar::handleGetExtendedYear(UErrorCode& status) {
  * @stable ICU 2.8
  */
 int32_t ChineseCalendar::handleGetMonthLength(int32_t extendedYear, int32_t month) const {
-    int32_t thisStart = handleComputeMonthStart(extendedYear, month, true) -
-        kEpochStartAsJulianDay + 1; // Julian day -> local days
+    int32_t thisStart = handleComputeMonthStart(extendedYear, month, true) -        kEpochStartAsJulianDay + 1; // Julian day -> local days
     int32_t nextStart = newMoonNear(thisStart + SYNODIC_GAP, true);
     return nextStart - thisStart;
 }
