patch:
diff --git a/icu4c/source/i18n/chnsecal.cpp b/icu4c/source/i18n/chnsecal.cpp
index 17d92ed1a3d..2976bde399c 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -231,9 +231,17 @@ int32_t ChineseCalendar::handleGetExtendedYear(UErrorCode& status) {
     if (newestStamp(UCAL_ERA, UCAL_YEAR, kUnset) <= fStamp[UCAL_EXTENDED_YEAR]) {
         year = internalGet(UCAL_EXTENDED_YEAR, 1); // Default to year 1
     } else {
-        int32_t cycle = internalGet(UCAL_ERA, 1) - 1; // 0-based cycle
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+         int32_t cycle = internalGet(UCAL_ERA, 1) - 1; // 0-based cycle
         // adjust to the instance specific epoch
-        year = cycle * 60 + internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        int64_t tmp = static_cast<int64_t>(cycle) * 60LL + internalGet(UCAL_YEAR, 1) - (fEpochYear - CHINESE_EPOCH_YEAR);
+        if (tmp > INT32_MAX) {
+            year = INT32_MAX;
+        } else if (tmp < INT32_MIN) {
+            year = INT32_MIN;
+        } else {
+            year = static_cast<int32_t>(tmp);
+        }
     }
     return year;
 }
