patch:
diff --git a/icu4c/source/i18n/calendar.cpp b/icu4c/source/i18n/calendar.cpp
index 5ef818b2fbe..8f0eaecd60b 100644
--- a/icu4c/source/i18n/calendar.cpp
+++ b/icu4c/source/i18n/calendar.cpp
@@ -2072,7 +2072,17 @@ void Calendar::roll(UCalendarDateFields field, int32_t amount, UErrorCode& statu
             return;
         }
     case UCAL_JULIAN_DAY:
-        set(field, internalGet(field) + amount);
+        {
+            int32_t currentValue = internalGet(field);
+            // Check for signed integer overflow and reasonable bounds
+            if ((amount > 0 && currentValue > INT32_MAX - amount) ||
+                (amount < 0 && currentValue < INT32_MIN - amount) ||
+                amount > 1000000 || amount < -1000000) {
+                status = U_ILLEGAL_ARGUMENT_ERROR;
+                return;
+            }
+            set(field, currentValue + amount);
+        }
         return;
     default:
         // Other fields cannot be rolled by this method
@@ -2129,7 +2139,17 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status

     switch (field) {
     case UCAL_ERA:
-        set(field, get(field, status) + amount);
+        {
+            int32_t currentValue = get(field, status);
+            // Check for signed integer overflow and reasonable bounds
+            if ((amount > 0 && currentValue > INT32_MAX - amount) ||
+                (amount < 0 && currentValue < INT32_MIN - amount) ||
+                amount > 1000000 || amount < -1000000) {
+                status = U_ILLEGAL_ARGUMENT_ERROR;
+                return;
+            }
+            set(field, currentValue + amount);
+        }
         pinField(UCAL_ERA, status);
         return;

@@ -2147,6 +2167,11 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status
         if (era == 0) {
           const char * calType = getType();
           if ( uprv_strcmp(calType,"gregorian")==0 || uprv_strcmp(calType,"roc")==0 || uprv_strcmp(calType,"coptic")==0 ) {
+            // Check for negation overflow (INT32_MIN cannot be negated)
+            if (amount == INT32_MIN) {
+              status = U_ILLEGAL_ARGUMENT_ERROR;
+              return;
+            }
             amount = -amount;
           }
         }
@@ -2159,7 +2184,17 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status
       {
         UBool oldLenient = isLenient();
         setLenient(true);
-        set(field, get(field, status) + amount);
+        {
+            int32_t currentValue = get(field, status);
+            // Check for signed integer overflow and reasonable bounds
+            if ((amount > 0 && currentValue > INT32_MAX - amount) ||
+                (amount < 0 && currentValue < INT32_MIN - amount) ||
+                amount > 1000000 || amount < -1000000) {
+                status = U_ILLEGAL_ARGUMENT_ERROR;
+                return;
+            }
+            set(field, currentValue + amount);
+        }
         pinField(UCAL_DAY_OF_MONTH, status);
         if(oldLenient==false) {
           complete(status); /* force recalculate */
