patch:
diff --git a/icu4c/source/i18n/calendar.cpp b/icu4c/source/i18n/calendar.cpp
index 5ef818b2fbe..a5326e4763e 100644
--- a/icu4c/source/i18n/calendar.cpp
+++ b/icu4c/source/i18n/calendar.cpp
@@ -2129,7 +2129,18 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status

     switch (field) {
     case UCAL_ERA:
-        set(field, get(field, status) + amount);
+        {
+            int32_t current = get(field, status);
+            int32_t result;
+            if (amount > 0 && current > INT32_MAX - amount) {
+                result = INT32_MAX;
+            } else if (amount < 0 && current < INT32_MIN - amount) {
+                result = INT32_MIN;
+            } else {
+                result = current + amount;
+            }
+            set(field, result);
+        }
         pinField(UCAL_ERA, status);
         return;

@@ -2147,7 +2158,12 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status
         if (era == 0) {
           const char * calType = getType();
           if ( uprv_strcmp(calType,"gregorian")==0 || uprv_strcmp(calType,"roc")==0 || uprv_strcmp(calType,"coptic")==0 ) {
-            amount = -amount;
+            if (amount == INT32_MIN) {
+                /* Negating INT32_MIN is undefined; clamp to INT32_MAX */
+                amount = INT32_MAX;
+            } else {
+                amount = -amount;
+            }
           }
         }
       }
@@ -2159,7 +2175,18 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status
       {
         UBool oldLenient = isLenient();
         setLenient(true);
-        set(field, get(field, status) + amount);
+        {
+            int32_t current = get(field, status);
+            int32_t result;
+            if (amount > 0 && current > INT32_MAX - amount) {
+                result = INT32_MAX;
+            } else if (amount < 0 && current < INT32_MIN - amount) {
+                result = INT32_MIN;
+            } else {
+                result = current + amount;
+            }
+            set(field, result);
+        }
         pinField(UCAL_DAY_OF_MONTH, status);
         if(oldLenient==false) {
           complete(status); /* force recalculate */
diff --git a/icu4c/source/i18n/gregoimp.cpp b/icu4c/source/i18n/gregoimp.cpp
index 1043113fdf1..bfea1dcb507 100644
--- a/icu4c/source/i18n/gregoimp.cpp
+++ b/icu4c/source/i18n/gregoimp.cpp
@@ -36,7 +36,15 @@ int32_t ClockMath::floorDivide(int32_t numerator, int32_t denominator,
                           int32_t* remainder) {
     auto quotient = floorDivide(numerator, denominator);
     if (remainder != nullptr) {
-      *remainder = numerator - (quotient * denominator);
+      int64_t prod = static_cast<int64_t>(quotient) * static_cast<int64_t>(denominator);
+      int64_t rem = static_cast<int64_t>(numerator) - prod;
+      if (rem > INT32_MAX) {
+        *remainder = INT32_MAX;
+      } else if (rem < INT32_MIN) {
+        *remainder = INT32_MIN;
+      } else {
+        *remainder = static_cast<int32_t>(rem);
+      }
     }
     return quotient;
 }
