patch:
diff --git a/icu4c/source/i18n/cecal.cpp b/icu4c/source/i18n/cecal.cpp
index b550684b798..559bd279d48 100644
--- a/icu4c/source/i18n/cecal.cpp
+++ b/icu4c/source/i18n/cecal.cpp
@@ -115,6 +115,16 @@ CECalendar::jdToCE(int32_t julianDay, int32_t jdEpochOffset, int32_t& year, int3
     int32_t c4; // number of 4 year cycle (1461 days)
     int32_t r4; // remainder of 4 year cycle, always positive

+    // Clamp julianDay to avoid signed integer overflow
+    // MIN_JULIAN and MAX_JULIAN are defined in gregoimp.h
+    const int32_t minJD = jdEpochOffset + (-0x7F000000); // MIN_JULIAN
+    const int32_t maxJD = jdEpochOffset + (0x7F000000);  // MAX_JULIAN
+    if (julianDay < minJD) {
+        julianDay = minJD;
+    } else if (julianDay > maxJD) {
+        julianDay = maxJD;
+    }
+
     c4 = ClockMath::floorDivide(julianDay - jdEpochOffset, 1461, &r4);

     year = 4 * c4 + (r4/365 - r4/1460); // 4 * <number of 4year cycle> + <years within the last cycle>
