patch:
diff --git a/icu4c/source/i18n/japancal.cpp b/icu4c/source/i18n/japancal.cpp
index 7e69b97a3af..bc3e178578f 100644
--- a/icu4c/source/i18n/japancal.cpp
+++ b/icu4c/source/i18n/japancal.cpp
@@ -228,7 +228,18 @@ void JapaneseCalendar::handleComputeFields(int32_t julianDay, UErrorCode& status
     int32_t eraIdx = gJapaneseEraRules->getEraIndex(year, internalGetMonth(status) + 1, internalGet(UCAL_DAY_OF_MONTH), status);

     internalSet(UCAL_ERA, eraIdx);
-    internalSet(UCAL_YEAR, year - gJapaneseEraRules->getStartYear(eraIdx, status) + 1);
+    
+    // Use int64_t for intermediate calculation to prevent overflow
+    int64_t startYear = gJapaneseEraRules->getStartYear(eraIdx, status);
+    int64_t yearDiff = (int64_t)year - startYear + 1;
+    
+    // Validate the result is within int32_t range before assignment
+    if (yearDiff > INT32_MAX || yearDiff < INT32_MIN) {
+        status = U_ILLEGAL_ARGUMENT_ERROR;
+        return;
+    }
+    
+    internalSet(UCAL_YEAR, (int32_t)yearDiff);
 }

