diff --git a/icu4c/source/common/messagepattern.cpp b/icu4c/source/common/messagepattern.cpp
index 9e318295f9a..9ba0cd9f7b4 100644
--- a/icu4c/source/common/messagepattern.cpp
+++ b/icu4c/source/common/messagepattern.cpp
@@ -744,7 +744,13 @@ MessagePattern::parseChoiceStyle(int32_t index, int32_t nestingLevel,
             errorCode=U_PATTERN_SYNTAX_ERROR;
             return 0;
         }
-        addPart(UMSGPAT_PART_TYPE_ARG_SELECTOR, index, 1, 0, errorCode);
+// FIXME: Crash CWE-834: Excessive Iteration
+        if(nestingLevel > 64) {  /* Limit maximal nesting to 64 */
+            setParseError(parseError, start);
+            errorCode=U_PARSE_ERROR;
+            return 0;
+        }
+         addPart(UMSGPAT_PART_TYPE_ARG_SELECTOR, index, 1, 0, errorCode);
         // Parse the message fragment.
         index=parseMessage(++index, 0, nestingLevel+1, UMSGPAT_ARG_TYPE_CHOICE, parseError, errorCode);
         if(U_FAILURE(errorCode)) {
