patch:
diff --git a/icu4c/source/i18n/chnsecal.cpp b/icu4c/source/i18n/chnsecal.cpp
index fb8e5191c52..aac2265272f 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -1045,7 +1045,19 @@ void ChineseCalendar::offsetMonth(int32_t newMoon, int32_t dayOfMonth, int32_t d
     }

     // Find the target dayOfMonth
-    int32_t jd = newMoon + kEpochStartAsJulianDay - 1 + dayOfMonth;
+    // Check for potential overflow before performing arithmetic
+    int64_t jd_temp = static_cast<int64_t>(newMoon) + kEpochStartAsJulianDay - 1 + dayOfMonth;
+    if (jd_temp < INT32_MIN || jd_temp > INT32_MAX) {
+        status = U_ILLEGAL_ARGUMENT_ERROR;
+        return;
+    }
+    int32_t jd = static_cast<int32_t>(jd_temp);
+    
+    // Additional check to ensure jd is within valid Julian day range
+    if (jd < MIN_JULIAN || jd > MAX_JULIAN) {
+        status = U_ILLEGAL_ARGUMENT_ERROR;
+        return;
+    }

     // Pin the dayOfMonth.  In this calendar all months are 29 or 30 days
     // so pinning just means handling dayOfMonth 30.
