patch:
diff --git a/src/hash.c b/src/hash.c
index 289f02a91..2aabcd806 100644
--- a/src/hash.c
+++ b/src/hash.c
@@ -636,10 +636,23 @@ ib_it_next(index_buckets_iter *it)
   uint32_t slid_ary_index = slid_bit_pos / IB_TYPE_BIT;
   it->ary_index = slid_ary_index + it->pos / IB_TYPE_BIT * it->bit;
   it->shift2 = (slid_ary_index + 1) * IB_TYPE_BIT - slid_bit_pos - 1;
-  it->ea_index = (ht_ib(it->h)[it->ary_index] >> it->shift2) & it->mask;
+  
+  /* Calculate maximum valid array index to prevent buffer overflow */
+  uint32_t ib_bit_val = ib_bit(it->h);
+  uint32_t max_ary_index = IB_INIT_BIT == 4 ?
+    ib_bit_to_capa(ib_bit_val) * 2 / IB_TYPE_BIT * ib_bit_val / 2 :
+    ib_bit_to_capa(ib_bit_val) / IB_TYPE_BIT * ib_bit_val;
+  
+  if (it->ary_index >= max_ary_index) {
+    it->ea_index = 0;
+  } else {
+    it->ea_index = (ht_ib(it->h)[it->ary_index] >> it->shift2) & it->mask;
+  }
   if (IB_TYPE_BIT - it->bit < it->shift2) {
     it->shift1 = IB_TYPE_BIT - it->shift2;
-    it->ea_index |= (ht_ib(it->h)[it->ary_index - 1] << it->shift1) & it->mask;
+    if (it->ary_index > 0 && it->ary_index - 1 < max_ary_index) {
+      it->ea_index |= (ht_ib(it->h)[it->ary_index - 1] << it->shift1) & it->mask;
+    }
   }
   else {
     it->shift1 = 0;
@@ -657,15 +670,26 @@ static void
 ib_it_set(index_buckets_iter *it, uint32_t ea_index)
 {
   uint32_t mask, i;
+  
+  /* Calculate maximum valid array index to prevent buffer overflow */
+  uint32_t ib_bit_val = ib_bit(it->h);
+  uint32_t max_ary_index = IB_INIT_BIT == 4 ?
+    ib_bit_to_capa(ib_bit_val) * 2 / IB_TYPE_BIT * ib_bit_val / 2 :
+    ib_bit_to_capa(ib_bit_val) / IB_TYPE_BIT * ib_bit_val;
+  
   it->ea_index = ea_index;
   if (it->shift1) {
     i = it->ary_index - 1;
-    mask = it->mask >> it->shift1;
-    ht_ib(it->h)[i] = (ht_ib(it->h)[i] & ~mask) | (ea_index >> it->shift1);
+    if (i < max_ary_index) {
+      mask = it->mask >> it->shift1;
+      ht_ib(it->h)[i] = (ht_ib(it->h)[i] & ~mask) | (ea_index >> it->shift1);
+    }
   }
   i = it->ary_index;
-  mask = it->mask << it->shift2;
-  ht_ib(it->h)[i] = (ht_ib(it->h)[i] & ~mask) | (ea_index << it->shift2);
+  if (i < max_ary_index) {
+    mask = it->mask << it->shift2;
+    ht_ib(it->h)[i] = (ht_ib(it->h)[i] & ~mask) | (ea_index << it->shift2);
+  }
 }

 static void
