patch:
diff --git a/src/hash.c b/src/hash.c
index 289f02a91..46283ce07 100644
--- a/src/hash.c
+++ b/src/hash.c
@@ -719,8 +719,12 @@ static uint32_t
 ib_byte_size_for(uint32_t ib_bit)
 {
   uint32_t ary_size = IB_INIT_BIT == 4 ?
-    ib_bit_to_capa(ib_bit) * 2 / IB_TYPE_BIT * ib_bit / 2 :
-    ib_bit_to_capa(ib_bit) / IB_TYPE_BIT * ib_bit;
+    ib_bit_to_capa(ib_bit) * ib_bit * 2 / IB_TYPE_BIT / 2 :
+    ib_bit_to_capa(ib_bit) * ib_bit / IB_TYPE_BIT;
+  /* Ensure we allocate at least one uint32_t */
+  if (ary_size == 0) {
+    ary_size = 1;
+  }
   return U32(sizeof(uint32_t) * ary_size);
 }
