patch:
diff --git a/src/string.c b/src/string.c
index 1bd3f6f8f..57314673e 100644
--- a/src/string.c
+++ b/src/string.c
@@ -2948,11 +2948,22 @@ str_bytesplice(mrb_state *mrb, mrb_value str, mrb_int idx1, mrb_int len1, mrb_va
 {
   struct RString *s = RSTRING(str);
   if (RSTR_LEN(s) < idx1 || RSTRING_LEN(replace) < idx2) {
-    mrb_raise(mrb, E_INDEX_ERROR, "index out of string");
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+     mrb_raise(mrb, E_INDEX_ERROR, "index out of string");
+  }
+  
+  // Check for integer overflow before performing idx1+len1
+  if (idx1 > 0 && len1 > 0 && idx1 > LONG_MAX - len1) {
+    mrb_raise(mrb, E_INDEX_ERROR, "integer overflow in string operation");
   }
   if (RSTR_LEN(s) <= idx1+len1) {
     len1 = RSTR_LEN(s) - idx1;
   }
+  
+  // Check for integer overflow before performing idx2+len2
+  if (idx2 > 0 && len2 > 0 && idx2 > LONG_MAX - len2) {
+    mrb_raise(mrb, E_INDEX_ERROR, "integer overflow in string operation");
+  }
   if (RSTRING_LEN(replace) <= idx2+len2) {
     len2 = RSTRING_LEN(replace) - idx2;
   }
