patch:
diff --git a/src/string.c b/src/string.c
index 1bd3f6f8f..394285f2f 100644
--- a/src/string.c
+++ b/src/string.c
@@ -2948,7 +2948,14 @@ str_bytesplice(mrb_state *mrb, mrb_value str, mrb_int idx1, mrb_int len1, mrb_va
 {
   struct RString *s = RSTRING(str);
   if (RSTR_LEN(s) < idx1 || RSTRING_LEN(replace) < idx2) {
-    mrb_raise(mrb, E_INDEX_ERROR, "index out of string");
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+     mrb_raise(mrb, E_INDEX_ERROR, "index out of string");
+  }
+  if ((len1 > 0 && idx1 > MRB_INT_MAX - len1) || (len1 < 0 && idx1 < MRB_INT_MIN - len1)) {
+    mrb_raise(mrb, E_INDEX_ERROR, "integer overflow in idx1+len1");
+  }
+  if ((len2 > 0 && idx2 > MRB_INT_MAX - len2) || (len2 < 0 && idx2 < MRB_INT_MIN - len2)) {
+    mrb_raise(mrb, E_INDEX_ERROR, "integer overflow in idx2+len2");
   }
   if (RSTR_LEN(s) <= idx1+len1) {
     len1 = RSTR_LEN(s) - idx1;
@@ -2967,6 +2974,9 @@ str_bytesplice(mrb_state *mrb, mrb_value str, mrb_int idx1, mrb_int len1, mrb_va
   }
   else { /* len1 < len2 */
     mrb_int slen = RSTR_LEN(s);
+    if ((len2 - len1) > (MRB_INT_MAX - slen)) {
+      mrb_raise(mrb, E_INDEX_ERROR, "integer overflow in slen+len2-len1");
+    }
     mrb_str_resize(mrb, str, slen+len2-len1);
     memmove(RSTR_PTR(s)+idx1+len2, RSTR_PTR(s)+idx1+len1, slen-(idx1+len1));
     memmove(RSTR_PTR(s)+idx1, RSTRING_PTR(replace)+idx2, len2);
@@ -2974,6 +2984,7 @@ str_bytesplice(mrb_state *mrb, mrb_value str, mrb_int idx1, mrb_int len1, mrb_va
   return str;
 }

+
 /*
  *  call-seq:
  *    bytesplice(index, length, str) -> string
