     if (task == Py_None) {
         if (_PyDict_DelItem_KnownHash(state->current_tasks, loop, hash) == -1) {
             return NULL;
@@ -2058,8 +2060,6 @@ swap_current_task(asyncio_state *state, PyObject *loop, PyObject *task)
         }
     }

