patch:
diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 4b84c1de66b..070c275ec00 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -2049,16 +2049,19 @@ swap_current_task(asyncio_state *state, PyObject *loop, PyObject *task)
     }

     if (task == Py_None) {
+        Py_XINCREF(prev_task);
         if (_PyDict_DelItem_KnownHash(state->current_tasks, loop, hash) == -1) {
+            Py_XDECREF(prev_task);
             return NULL;
         }
     } else {
+        Py_XINCREF(prev_task);
         if (_PyDict_SetItem_KnownHash(state->current_tasks, loop, task, hash) == -1) {
+            Py_XDECREF(prev_task);
             return NULL;
         }
     }

-    Py_INCREF(prev_task);

     return prev_task;
 }
