patch:
diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 4b84c1de66b..b00950c7f9e 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -2049,17 +2049,18 @@ swap_current_task(asyncio_state *state, PyObject *loop, PyObject *task)
     }

     if (task == Py_None) {
+        Py_INCREF(prev_task);
         if (_PyDict_DelItem_KnownHash(state->current_tasks, loop, hash) == -1) {
+            Py_DECREF(prev_task);
             return NULL;
         }
     } else {
         if (_PyDict_SetItem_KnownHash(state->current_tasks, loop, task, hash) == -1) {
             return NULL;
         }
+        Py_INCREF(prev_task);
     }

-    Py_INCREF(prev_task);
-
     return prev_task;
 }
