diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 4b84c1de66b..703ea3890df 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -2048,17 +2048,20 @@ swap_current_task(asyncio_state *state, PyObject *loop, PyObject *task)
         prev_task = Py_None;
     }

+    Py_INCREF(prev_task);
+
     if (task == Py_None) {
         if (_PyDict_DelItem_KnownHash(state->current_tasks, loop, hash) == -1) {
+            Py_DECREF(prev_task);
             return NULL;
         }
     } else {
         if (_PyDict_SetItem_KnownHash(state->current_tasks, loop, task, hash) == -1) {
+            Py_DECREF(prev_task);
             return NULL;
         }
-    }
-
-    Py_INCREF(prev_task);
+// FIXME: Crash CWE-416: Use After Free
+     }

     return prev_task;
 }
