patch:
diff --git a/Modules/_sre/sre.c b/Modules/_sre/sre.c
index 3f11916f9e1..37809fbfb8d 100644
--- a/Modules/_sre/sre.c
+++ b/Modules/_sre/sre.c
@@ -1566,7 +1566,10 @@ _sre_template_impl(PyObject *module, PyObject *pattern, PyObject *template)

 bad_template:
     PyErr_SetString(PyExc_TypeError, "invalid template");
-    Py_XDECREF(self);
+    if (self) {
+        // self may be partially initialized, so do not call Py_XDECREF (which would call tp_dealloc)
+        PyObject_GC_Del(self);
+    }
     return NULL;
 }
