patch:
diff --git a/Objects/longobject.c b/Objects/longobject.c
index fae70dd13bb..3bd134361f7 100644
--- a/Objects/longobject.c
+++ b/Objects/longobject.c
@@ -6172,6 +6172,7 @@ long_vectorcall(PyObject *type, PyObject * const*args,
         default:
             return PyErr_Format(PyExc_TypeError,
                                 "int expected at most 2 argument%s, got %zd",
+                                nargs == 1 ? "" : "s",
                                 nargs);
     }
 }
diff --git a/Objects/unicodeobject.c b/Objects/unicodeobject.c
index 4b03cc3f4da..6b242f28d4b 100644
--- a/Objects/unicodeobject.c
+++ b/Objects/unicodeobject.c
@@ -2384,6 +2384,10 @@ unicode_fromformat_write_cstr(_PyUnicodeWriter *writer, const char *str,
     PyObject *unicode;
     int res;

+    if (str == NULL) {
+        str = "(null)";
+    }
+
     if (precision == -1) {
         length = strlen(str);
     }
@@ -2411,6 +2415,10 @@ unicode_fromformat_write_wcstr(_PyUnicodeWriter *writer, const wchar_t *str,
     PyObject *unicode;
     int res;

+    if (str == NULL) {
+        str = L"(null)";
+    }
+
     if (precision == -1) {
         length = wcslen(str);
     }
