patch:
diff --git a/Objects/object.c b/Objects/object.c
index 275aa6713c8..f18688d03ca 100644
--- a/Objects/object.c
+++ b/Objects/object.c
@@ -960,9 +960,26 @@ PyObject_RichCompare(PyObject *v, PyObject *w, int op)
         return NULL;
     }
     if (_Py_EnterRecursiveCallTstate(tstate, " in comparison")) {
+// FIXME: Crash CWE-416: Use After Free
+         return NULL;
+    }
+    
+    // Create temporary container to prevent garbage collection during comparison
+    PyObject *temp_holder = PyTuple_New(2);
+    if (temp_holder == NULL) {
+        _Py_LeaveRecursiveCallTstate(tstate);
         return NULL;
     }
+    Py_INCREF(v);
+    Py_INCREF(w);
+    PyTuple_SET_ITEM(temp_holder, 0, v);
+    PyTuple_SET_ITEM(temp_holder, 1, w);
+    
     PyObject *res = do_richcompare(tstate, v, w, op);
+    
+    // Release temporary container
+    Py_DECREF(temp_holder);
+    
     _Py_LeaveRecursiveCallTstate(tstate);
     return res;
 }
