patch:
diff --git a/Objects/object.c b/Objects/object.c
index 275aa6713c8..363b90201f6 100644
--- a/Objects/object.c
+++ b/Objects/object.c
@@ -959,11 +959,17 @@ PyObject_RichCompare(PyObject *v, PyObject *w, int op)
         }
         return NULL;
     }
+    Py_INCREF(v);
+    Py_INCREF(w);
     if (_Py_EnterRecursiveCallTstate(tstate, " in comparison")) {
-        return NULL;
+        Py_DECREF(v);
+        Py_DECREF(w);
+         return NULL;
     }
     PyObject *res = do_richcompare(tstate, v, w, op);
     _Py_LeaveRecursiveCallTstate(tstate);
+    Py_DECREF(v);
+    Py_DECREF(w);
     return res;
 }
