diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 5cf9eba243b..a3485f7e473 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -296,7 +296,6 @@ initContext(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry)
     self->ctxEntry = entry;
     self->subt = 0;
     self->previous = pObj->currentProfilerContext;
-    pObj->currentProfilerContext = self;
     ++entry->recursionLevel;
     if ((pObj->flags & POF_SUBCALLS) && self->previous) {
         /* find or create an entry for me in my caller's entry */
@@ -308,12 +307,16 @@ initContext(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry)
             ++subentry->recursionLevel;
     }
     self->t0 = call_timer(pObj);
+    pObj->currentProfilerContext = self;
 }

 static void
 Stop(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry)
 {
+    ProfilerContext *saved_context = pObj->currentProfilerContext;
+    pObj->currentProfilerContext = self->previous;
     PyTime_t tt = call_timer(pObj) - self->t0;
+    pObj->currentProfilerContext = saved_context;
     PyTime_t it = tt - self->subt;
     if (self->previous)
         self->previous->subt += tt;
