patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 5cf9eba243b..367dfbbef6b 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -755,15 +755,25 @@ profiler_enable(ProfilerObject *self, PyObject *args, PyObject *kwds)
 static void
 flush_unmatched(ProfilerObject *pObj)
 {
+    /* Collect all contexts first to avoid recursive issues */
+    ProfilerContext *contexts_to_free = NULL;
     while (pObj->currentProfilerContext) {
         ProfilerContext *pContext = pObj->currentProfilerContext;
-        ProfilerEntry *profEntry= pContext->ctxEntry;
-        if (profEntry)
-            Stop(pObj, pContext, profEntry);
-        else
-            pObj->currentProfilerContext = pContext->previous;
-        if (pContext)
-            PyMem_Free(pContext);
+        pObj->currentProfilerContext = pContext->previous;
+        /* Add to list of contexts to free */
+        pContext->previous = contexts_to_free;
+        contexts_to_free = pContext;
+    }
+    
+    /* Now process and free all contexts */
+    while (contexts_to_free) {
+        ProfilerContext *pContext = contexts_to_free;
+        contexts_to_free = pContext->previous;
+        ProfilerEntry *profEntry = pContext->ctxEntry;
+        if (profEntry) {
+            /* Process the entry without calling Stop to avoid recursion */
+            ++profEntry->callcount;
+        }
     }

 }
