patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 5cf9eba243b..954dbb0b96f 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -291,7 +291,7 @@ static void clearEntries(ProfilerObject *pObj)
 }

 static void
-initContext(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry)
+initContext(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry, PyTime_t t0)
 {
     self->ctxEntry = entry;
     self->subt = 0;
@@ -307,13 +307,13 @@ initContext(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry)
         if (subentry)
             ++subentry->recursionLevel;
     }
-    self->t0 = call_timer(pObj);
+    self->t0 = t0;
 }

 static void
-Stop(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry)
+Stop(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry, PyTime_t t1)
 {
-    PyTime_t tt = call_timer(pObj) - self->t0;
+    PyTime_t tt = t1 - self->t0;
     PyTime_t it = tt - self->subt;
     if (self->previous)
         self->previous->subt += tt;
@@ -376,7 +376,8 @@ ptrace_enter_call(PyObject *self, void *key, PyObject *userObj)
             goto restorePyerr;
         }
     }
-    initContext(pObj, pContext, profEntry);
+    PyTime_t t0 = call_timer(pObj);
+    initContext(pObj, pContext, profEntry, t0);

 restorePyerr:
     PyErr_SetRaisedException(exc);
@@ -389,13 +390,15 @@ ptrace_leave_call(PyObject *self, void *key)
     ProfilerObject *pObj = (ProfilerObject*)self;
     ProfilerEntry *profEntry;
     ProfilerContext *pContext;
+    PyTime_t t1;

+    t1 = call_timer(pObj);
     pContext = pObj->currentProfilerContext;
     if (pContext == NULL)
         return;
     profEntry = getEntry(pObj, key);
     if (profEntry) {
-        Stop(pObj, pContext, profEntry);
+        Stop(pObj, pContext, profEntry, t1);
     }
     else {
         pObj->currentProfilerContext = pContext->previous;
@@ -759,7 +762,7 @@ flush_unmatched(ProfilerObject *pObj)
         ProfilerContext *pContext = pObj->currentProfilerContext;
         ProfilerEntry *profEntry= pContext->ctxEntry;
         if (profEntry)
-            Stop(pObj, pContext, profEntry);
+            Stop(pObj, pContext, profEntry, call_timer(pObj));
         else
             pObj->currentProfilerContext = pContext->previous;
         if (pContext)
