patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 5cf9eba243b..71726919821 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -757,14 +757,22 @@ flush_unmatched(ProfilerObject *pObj)
 {
     while (pObj->currentProfilerContext) {
         ProfilerContext *pContext = pObj->currentProfilerContext;
-        ProfilerEntry *profEntry= pContext->ctxEntry;
-        if (profEntry)
+        ProfilerEntry *profEntry = pContext->ctxEntry;
+        if (profEntry) {
+            /* Stop will update pObj->currentProfilerContext and free pContext */
             Stop(pObj, pContext, profEntry);
-        else
+        } else {
             pObj->currentProfilerContext = pContext->previous;
-        if (pContext)
             PyMem_Free(pContext);
+        }
+    }
+    /* Free all contexts in the freelist to avoid use-after-free */
+    while (pObj->freelistProfilerContext) {
+        ProfilerContext *c = pObj->freelistProfilerContext;
+        pObj->freelistProfilerContext = c->previous;
+        PyMem_Free(c);
     }
+    pObj->freelistProfilerContext = NULL;

 }
