patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 5cf9eba243b..49107543662 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -758,12 +758,13 @@ flush_unmatched(ProfilerObject *pObj)
     while (pObj->currentProfilerContext) {
         ProfilerContext *pContext = pObj->currentProfilerContext;
         ProfilerEntry *profEntry= pContext->ctxEntry;
-        if (profEntry)
+        if (profEntry) {
             Stop(pObj, pContext, profEntry);
-        else
+            /* Don't free pContext after Stop: call stack may still be using it. */
+        } else {
             pObj->currentProfilerContext = pContext->previous;
-        if (pContext)
             PyMem_Free(pContext);
+        }
     }

 }
