index 5cf9eba243b..6f75dff801a 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -757,13 +757,14 @@ flush_unmatched(ProfilerObject *pObj)
 {
     while (pObj->currentProfilerContext) {
         ProfilerContext *pContext = pObj->currentProfilerContext;
-        ProfilerEntry *profEntry= pContext->ctxEntry;
-        if (profEntry)
+        ProfilerEntry *profEntry = pContext->ctxEntry;
+        if (profEntry) {
             Stop(pObj, pContext, profEntry);
-        else
+            /* Stop() advances currentProfilerContext and manages memory */
+        } else {
             pObj->currentProfilerContext = pContext->previous;
-        if (pContext)
             PyMem_Free(pContext);
+        }
     }

 }
