patch:
diff --git a/Objects/object.c b/Objects/object.c
index b7730475ac3..29cdce724dd 100644
--- a/Objects/object.c
+++ b/Objects/object.c
@@ -970,11 +970,17 @@ PyObject_RichCompare(PyObject *v, PyObject *w, int op)
         }
         return NULL;
     }
+    Py_INCREF(v);
+    Py_INCREF(w);
     if (_Py_EnterRecursiveCallTstate(tstate, " in comparison")) {
-        return NULL;
+         Py_DECREF(v);
+         Py_DECREF(w);
+         return NULL;
     }
     PyObject *res = do_richcompare(tstate, v, w, op);
     _Py_LeaveRecursiveCallTstate(tstate);
+    Py_DECREF(v);
+    Py_DECREF(w);
     return res;
 }
