diff --git a/Objects/listobject.c b/Objects/listobject.c
index 6829d5d2865..655c75ec1c9 100644
--- a/Objects/listobject.c
+++ b/Objects/listobject.c
@@ -3711,6 +3711,25 @@ list_ass_subscript(PyObject* _self, PyObject* item, PyObject* value)
                 return 0;
             }

+            // Check the range of start/step so that no out-of-bounds access occurs
+            if (step > 0) {
+                if ((start < 0 || (size_t)start >= (size_t)Py_SIZE(self)) ||
+                    (start + (slicelength-1) * step < 0) ||
+                    (size_t)(start + (slicelength-1) * step) >= (size_t)Py_SIZE(self)) {
+                    PyErr_SetString(PyExc_IndexError, "slice assignment index out of range");
+                    Py_DECREF(seq);
+                    return -1;
+                }
+            } else if (step < 0) {
+                if ((start < 0 || (size_t)start >= (size_t)Py_SIZE(self)) ||
+                    (start + (slicelength-1) * step < 0) ||
+                    (size_t)(start + (slicelength-1) * step) >= (size_t)Py_SIZE(self)) {
+                    PyErr_SetString(PyExc_IndexError, "slice assignment index out of range");
+                    Py_DECREF(seq);
+                    return -1;
+                }
+            }
+
             garbage = (PyObject**)
                 PyMem_Malloc(slicelength*sizeof(PyObject*));
             if (!garbage) {
@@ -3721,7 +3740,8 @@ list_ass_subscript(PyObject* _self, PyObject* item, PyObject* value)

             selfitems = self->ob_item;
             seqitems = PySequence_Fast_ITEMS(seq);
-            for (cur = start, i = 0; i < slicelength;
+// FIXME: Crash CWE-125: Out-of-bounds Read
+             for (cur = start, i = 0; i < slicelength;
                  cur += (size_t)step, i++) {
                 garbage[i] = selfitems[cur];
                 ins = Py_NewRef(seqitems[i]);
