patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 257de4387c0..8432ecbba39 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -56,6 +56,7 @@ typedef struct {
 #define POF_ENABLED     0x001
 #define POF_SUBCALLS    0x002
 #define POF_BUILTINS    0x004
+#define POF_IN_TIMER    0x008
 #define POF_NOMEMORY    0x100

 /*[clinic input]
@@ -114,12 +115,21 @@ static _PyTime_t CallExternalTimer(ProfilerObject *pObj)
 static inline _PyTime_t
 call_timer(ProfilerObject *pObj)
 {
+    if (pObj->flags & POF_IN_TIMER) {
+        /* Prevent re-entrant timer calls that could trigger profiler_disable */
+        return _PyTime_GetPerfCounter();
+    }
+    
+    pObj->flags |= POF_IN_TIMER;
+    _PyTime_t result;
     if (pObj->externalTimer != NULL) {
-        return CallExternalTimer(pObj);
+        result = CallExternalTimer(pObj);
     }
     else {
-        return _PyTime_GetPerfCounter();
+        result = _PyTime_GetPerfCounter();
     }
+    pObj->flags &= ~POF_IN_TIMER;
+    return result;
 }


@@ -773,6 +783,11 @@ Stop collecting profiling information.\n\
 static PyObject*
 profiler_disable(ProfilerObject *self, PyObject* noarg)
 {
+    if (self->flags & POF_IN_TIMER) {
+        /* Defer disable until timer operation completes */
+        Py_RETURN_NONE;
+    }
+    
     if (self->flags & POF_ENABLED) {
         PyObject* result = NULL;
         PyObject* monitoring = _PyImport_GetModuleAttrString("sys", "monitoring");
