patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 257de4387c0..cc097923543 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -303,13 +303,26 @@ initContext(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry)
         if (subentry)
             ++subentry->recursionLevel;
     }
-    self->t0 = call_timer(pObj);
+    _PyTime_t timer_result = call_timer(pObj);
+    /* Check if the context is still valid after the timer call,
+       as external timers can trigger profiler disable */
+    if (pObj->currentProfilerContext == self) {
+        self->t0 = timer_result;
+    }
 }

 static void
 Stop(ProfilerObject *pObj, ProfilerContext *self, ProfilerEntry *entry)
 {
-    _PyTime_t tt = call_timer(pObj) - self->t0;
+    _PyTime_t timer_result = call_timer(pObj);
+    /* Check if the context is still valid after the timer call,
+       as external timers can trigger profiler disable */
+    if (pObj->currentProfilerContext != self) {
+        /* Context was freed during timer call, nothing to do */
+        return;
+    }
+    
+    _PyTime_t tt = timer_result - self->t0;
     _PyTime_t it = tt - self->subt;
     if (self->previous)
         self->previous->subt += tt;
@@ -385,20 +398,33 @@ ptrace_leave_call(PyObject *self, void *key)
     ProfilerObject *pObj = (ProfilerObject*)self;
     ProfilerEntry *profEntry;
     ProfilerContext *pContext;
+    ProfilerContext *pPrevious;
+    int was_enabled;

     pContext = pObj->currentProfilerContext;
     if (pContext == NULL)
         return;
+    /* Save the previous context and enabled state before any calls */
+    pPrevious = pContext->previous;
+    was_enabled = pObj->flags & POF_ENABLED;
+    
     profEntry = getEntry(pObj, key);
     if (profEntry) {
         Stop(pObj, pContext, profEntry);
     }
     else {
-        pObj->currentProfilerContext = pContext->previous;
+        pObj->currentProfilerContext = pPrevious;
+    }
+    
+    /* Only manipulate pContext if profiler is still enabled and context wasn't freed */
+    if ((pObj->flags & POF_ENABLED) && was_enabled && 
+        pObj->currentProfilerContext == pPrevious) {
+        /* pContext is still valid, put it into the free list */
+        pContext->previous = pObj->freelistProfilerContext;
+        pObj->freelistProfilerContext = pContext;
     }
-    /* put pContext into the free list */
-    pContext->previous = pObj->freelistProfilerContext;
-    pObj->freelistProfilerContext = pContext;
+    /* If profiler was disabled or pContext was freed, it was already handled 
+       in flush_unmatched, so we don't need to do anything */
 }

 static int
