patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 257de4387c0..93242d8fd96 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -754,12 +754,15 @@ flush_unmatched(ProfilerObject *pObj)
     while (pObj->currentProfilerContext) {
         ProfilerContext *pContext = pObj->currentProfilerContext;
         ProfilerEntry *profEntry= pContext->ctxEntry;
-        if (profEntry)
+        if (profEntry) {
             Stop(pObj, pContext, profEntry);
-        else
+            /* Do not free: Stop() unwinds stack and we should not access/free again. */
+            continue;
+        }
+        else {
             pObj->currentProfilerContext = pContext->previous;
-        if (pContext)
             PyMem_Free(pContext);
+        }
     }

 }
