patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 257de4387c0..08071ceea5f 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -754,12 +754,14 @@ flush_unmatched(ProfilerObject *pObj)
     while (pObj->currentProfilerContext) {
         ProfilerContext *pContext = pObj->currentProfilerContext;
         ProfilerEntry *profEntry= pContext->ctxEntry;
-        if (profEntry)
+        if (profEntry) {
             Stop(pObj, pContext, profEntry);
-        else
+            // Stop() updates currentProfilerContext, and pContext may now be invalid.
+            // Do NOT free pContext here.
+        } else {
             pObj->currentProfilerContext = pContext->previous;
-        if (pContext)
             PyMem_Free(pContext);
+        }
     }

 }
