patch:
diff --git a/Modules/_lsprof.c b/Modules/_lsprof.c
index 257de4387c0..8b86cb612d8 100644
--- a/Modules/_lsprof.c
+++ b/Modules/_lsprof.c
@@ -753,13 +753,21 @@ flush_unmatched(ProfilerObject *pObj)
 {
     while (pObj->currentProfilerContext) {
         ProfilerContext *pContext = pObj->currentProfilerContext;
-        ProfilerEntry *profEntry= pContext->ctxEntry;
-        if (profEntry)
+        ProfilerEntry *profEntry = pContext->ctxEntry;
+        if (profEntry) {
+            /* Stop will update currentProfilerContext and put pContext into the freelist */
             Stop(pObj, pContext, profEntry);
-        else
+            /* DO NOT free pContext here; it is now in the freelist and will be freed below. */
+        } else {
             pObj->currentProfilerContext = pContext->previous;
-        if (pContext)
             PyMem_Free(pContext);
+        }
+    }
+    /* Now free all contexts in the freelist */
+    while (pObj->freelistProfilerContext) {
+        ProfilerContext *c = pObj->freelistProfilerContext;
+        pObj->freelistProfilerContext = c->previous;
+        PyMem_Free(c);
     }

 }
