patch:
diff --git a/Modules/readline.c b/Modules/readline.c
index 35655c70a46..0dbe17e4c6e 100644
--- a/Modules/readline.c
+++ b/Modules/readline.c
@@ -362,8 +362,14 @@ readline_append_history_file_impl(PyObject *module, int nelements,
         filename_bytes = NULL;
         filename = NULL;
     }
-    errno = err = append_history(
-        nelements - libedit_append_replace_history_offset, filename);
+    int adjusted_nelements = nelements - libedit_append_replace_history_offset;
+    if (adjusted_nelements < 0) {
+        PyErr_SetString(PyExc_ValueError,
+                        "nelements must be positive");
+        Py_XDECREF(filename_bytes);
+        return NULL;
+    }
+    errno = err = append_history(adjusted_nelements, filename);
     if (!err && _history_length >= 0)
         history_truncate_file(filename, _history_length);
     Py_XDECREF(filename_bytes);
