patch:
diff --git a/Modules/readline.c b/Modules/readline.c
index 35655c70a46..421b0b24021 100644
--- a/Modules/readline.c
+++ b/Modules/readline.c
@@ -362,8 +362,13 @@ readline_append_history_file_impl(PyObject *module, int nelements,
         filename_bytes = NULL;
         filename = NULL;
     }
-    errno = err = append_history(
-        nelements - libedit_append_replace_history_offset, filename);
+    int n_append = nelements - libedit_append_replace_history_offset;
+    if (n_append <= 0) {
+        PyErr_SetString(PyExc_ValueError, "invalid nelements value for append_history");
+        Py_XDECREF(filename_bytes);
+        return NULL;
+    }
+    errno = err = append_history(n_append, filename);
     if (!err && _history_length >= 0)
         history_truncate_file(filename, _history_length);
     Py_XDECREF(filename_bytes);
