patch:
diff --git a/Modules/readline.c b/Modules/readline.c
index 35655c70a46..a683228579a 100644
--- a/Modules/readline.c
+++ b/Modules/readline.c
@@ -358,13 +358,29 @@ readline_append_history_file_impl(PyObject *module, int nelements,
         if (!PyUnicode_FSConverter(filename_obj, &filename_bytes))
             return NULL;
         filename = PyBytes_AS_STRING(filename_bytes);
+        
+        /* Validate filename pointer before using it */
+        if (filename == NULL) {
+            Py_DECREF(filename_bytes);
+            PyErr_SetString(PyExc_ValueError, "invalid filename conversion");
+            return NULL;
+        }
     } else {
         filename_bytes = NULL;
-        filename = NULL;
+// FIXME: Crash CWE-416: Use After Free
+         filename = NULL;
     }
+    
+    /* Defensive check for valid nelements parameter */
+    if (nelements < 0) {
+        Py_XDECREF(filename_bytes);
+        PyErr_SetString(PyExc_ValueError, "nelements must be non-negative");
+        return NULL;
+    }
+    
     errno = err = append_history(
         nelements - libedit_append_replace_history_offset, filename);
-    if (!err && _history_length >= 0)
+    if (!err && _history_length >= 0 && filename != NULL)
         history_truncate_file(filename, _history_length);
     Py_XDECREF(filename_bytes);
     errno = err;
