patch:
diff --git a/Objects/frameobject.c b/Objects/frameobject.c
index 9f1c031dcb9..734a12c8d14 100644
--- a/Objects/frameobject.c
+++ b/Objects/frameobject.c
@@ -315,8 +315,20 @@ framelocalsproxy_new(PyTypeObject *type, PyObject *args, PyObject *kwds)
         return NULL;
     }

+    if (PyTuple_Size(args) < 1) {
+        PyErr_SetString(PyExc_TypeError, "framelocalsproxy() missing required argument");
+        Py_DECREF(self);
+        return NULL;
+    }
+
     PyFrameObject *frame = (PyFrameObject*)PyTuple_GET_ITEM(args, 0);
-    assert(PyFrame_Check(frame));
+    if (frame == NULL || !PyFrame_Check(frame)) {
+        PyErr_SetString(PyExc_TypeError, "framelocalsproxy() argument must be a frame object");
+        Py_DECREF(self);
+        return NULL;
+    }
+
+    // Now safe to use Py_NewRef since frame is validated

     ((PyFrameLocalsProxyObject*)self)->frame = (PyFrameObject*)Py_NewRef(frame);
