patch:
diff --git a/Objects/frameobject.c b/Objects/frameobject.c
index 9f1c031dcb9..5c6c9025dae 100644
--- a/Objects/frameobject.c
+++ b/Objects/frameobject.c
@@ -316,7 +316,18 @@ framelocalsproxy_new(PyTypeObject *type, PyObject *args, PyObject *kwds)
     }

     PyFrameObject *frame = (PyFrameObject*)PyTuple_GET_ITEM(args, 0);
-    assert(PyFrame_Check(frame));
+    // Defensive: check args is a tuple and has at least one item
+    if (!PyTuple_Check(args) || PyTuple_GET_SIZE(args) < 1) {
+        Py_DECREF(self);
+        PyErr_SetString(PyExc_TypeError, "framelocalsproxy_new expects a tuple with at least one argument");
+        return NULL;
+    }
+    frame = (PyFrameObject*)PyTuple_GET_ITEM(args, 0);
+    if (frame == NULL || !PyFrame_Check(frame)) {
+        Py_DECREF(self);
+        PyErr_SetString(PyExc_TypeError, "framelocalsproxy_new expects a frame object as first argument");
+        return NULL;
+    }

     ((PyFrameLocalsProxyObject*)self)->frame = (PyFrameObject*)Py_NewRef(frame);
