patch:
diff --git a/Modules/_cursesmodule.c b/Modules/_cursesmodule.c
index d32cff66144..f64c2f74811 100644
--- a/Modules/_cursesmodule.c
+++ b/Modules/_cursesmodule.c
@@ -3259,6 +3259,16 @@ _curses_initscr_impl(PyObject *module)
         return (PyObject *)PyCursesWindow_New(stdscr, NULL);
     }

+    /* Check that TERM is set and stdin is a terminal */
+    if (getenv("TERM") == NULL) {
+        PyErr_SetString(PyCursesError, "Environment variable TERM not set");
+        return NULL;
+    }
+    if (!isatty(fileno(stdin))) {
+        PyErr_SetString(PyCursesError, "stdin is not a tty");
+        return NULL;
+    }
+
     win = initscr();

     if (win == NULL) {
